import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Logging middleware
app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  // Error handling middleware
  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // Setup Vite in development, otherwise serve static
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // Use PORT from env or fallback to 5000
  const port = parseInt(process.env.PORT || "5000", 10);

  // âœ… Donâ€™t force "0.0.0.0" on Windows â€” just use localhost
  server.listen(port, () => {
    log(`Server running at http://localhost:${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertPredictionSchema } from "@shared/schema";
import { spawn } from "child_process";
import path from "path";

export async function registerRoutes(app: Express): Promise<Server> {
  // User profile routes
  app.get("/api/profile/:id", async (req, res) => {
    try {
      const profile = await storage.getUserProfile(req.params.id);
      if (!profile) {
        return res.status(404).json({ error: "Profile not found" });
      }
      res.json(profile);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch profile" });
    }
  });

  app.post("/api/profile", async (req, res) => {
    try {
      const profile = await storage.createUserProfile(req.body);
      res.json(profile);
    } catch (error) {
      res.status(400).json({ error: "Failed to create profile" });
    }
  });

  // Prediction routes
  app.get("/api/predictions/:userId", async (req, res) => {
    try {
      const predictions = await storage.getUserPredictions(req.params.userId);
      res.json(predictions);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch predictions" });
    }
  });

  app.post("/api/predictions", async (req, res) => {
    try {
      const validatedData = insertPredictionSchema.parse(req.body);
      const prediction = await storage.createPrediction(validatedData);
      res.json(prediction);
    } catch (error) {
      res.status(400).json({ error: "Failed to create prediction" });
    }
  });

  app.get("/api/predictions/:userId/search", async (req, res) => {
    try {
      const query = req.query.q as string;
      if (!query) {
        return res.status(400).json({ error: "Search query required" });
      }
      const results = await storage.searchPredictions(req.params.userId, query);
      res.json(results);
    } catch (error) {
      res.status(500).json({ error: "Failed to search predictions" });
    }
  });

  // ML Prediction - Use your trained fish_stock_model.pkl directly
  app.post("/api/ml-predict", async (req, res) => {
    try {
      const { query } = req.body;

      if (!query || typeof query !== 'string') {
        return res.status(400).json({ error: "Query is required" });
      }

      // Call Python script directly with your trained model
      const pythonScriptPath = path.join(process.cwd(), "client", "src", "Backend", "predict_single.py");

      const pythonProcess = spawn("python", [pythonScriptPath], {
        stdio: ['pipe', 'pipe', 'pipe']
      });

      // Send query data to Python script
      pythonProcess.stdin.write(JSON.stringify({ query }));
      pythonProcess.stdin.end();

      let stdout = '';
      let stderr = '';

      pythonProcess.stdout.on('data', (data) => {
        stdout += data.toString();
      });

      pythonProcess.stderr.on('data', (data) => {
        stderr += data.toString();
      });

      pythonProcess.on('close', (code) => {
        if (code === 0 && stdout) {
          try {
            const result = JSON.parse(stdout);
            res.json(result);
          } catch (parseError) {
            console.error("Error parsing Python output:", parseError);
            res.status(500).json({ error: "Failed to parse model output" });
          }
        } else {
          // Check if it's a validation error (exit code 1) vs system error
          if (code === 1 && stdout) {
            try {
              const errorResult = JSON.parse(stdout);
              // Return 400 for validation errors (non-fish species)
              res.status(400).json(errorResult);
              return;
            } catch (parseError) {
              // If can't parse validation error, fall through to system error
            }
          }

          console.error("Python process error:", stderr);
          res.status(500).json({ 
            error: "Model prediction failed",
            model_used: false
          });
        }
      });

      // Timeout after 10 seconds
      setTimeout(() => {
        pythonProcess.kill('SIGTERM');
        if (!res.headersSent) {
          res.status(500).json({ error: "Prediction timeout" });
        }
      }, 10000);

    } catch (error) {
      console.error("ML prediction error:", error);
      res.status(500).json({ 
        error: "Failed to generate prediction",
        message: error instanceof Error ? error.message : "Unknown error"
      });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import { type UserProfile, type InsertUserProfile, type Prediction, type InsertPrediction } from "@shared/schema";
import { randomUUID } from "crypto";

// Storage interface for user profiles and predictions
export interface IStorage {
  getUserProfile(id: string): Promise<UserProfile | undefined>;
  getUserProfileByEmail(email: string): Promise<UserProfile | undefined>;
  createUserProfile(profile: InsertUserProfile): Promise<UserProfile>;
  updateUserProfile(id: string, profile: Partial<InsertUserProfile>): Promise<UserProfile | undefined>;

  getPrediction(id: string): Promise<Prediction | undefined>;
  getUserPredictions(userId: string): Promise<Prediction[]>;
  createPrediction(prediction: InsertPrediction): Promise<Prediction>;
  searchPredictions(userId: string, query: string): Promise<Prediction[]>;
}

export class MemStorage implements IStorage {
  private userProfiles: Map<string, UserProfile>;
  private predictions: Map<string, Prediction>;

  constructor() {
    this.userProfiles = new Map();
    this.predictions = new Map();
  }

  async getUserProfile(id: string): Promise<UserProfile | undefined> {
    return this.userProfiles.get(id);
  }

  async getUserProfileByEmail(email: string): Promise<UserProfile | undefined> {
    return Array.from(this.userProfiles.values()).find(
      (profile) => profile.email === email,
    );
  }

  async createUserProfile(insertProfile: InsertUserProfile): Promise<UserProfile> {
    const id = randomUUID();
    const profile: UserProfile = { 
      id,
      email: insertProfile.email,
      fullName: insertProfile.fullName || null,
      organization: insertProfile.organization || null,
      role: insertProfile.role || null,
      createdAt: new Date(),
      updatedAt: new Date()
    };
    this.userProfiles.set(id, profile);
    return profile;
  }

  async updateUserProfile(id: string, updates: Partial<InsertUserProfile>): Promise<UserProfile | undefined> {
    const existing = this.userProfiles.get(id);
    if (!existing) return undefined;

    const updated: UserProfile = { 
      ...existing, 
      ...updates, 
      updatedAt: new Date() 
    };
    this.userProfiles.set(id, updated);
    return updated;
  }

  async getPrediction(id: string): Promise<Prediction | undefined> {
    return this.predictions.get(id);
  }

  async getUserPredictions(userId: string): Promise<Prediction[]> {
    return Array.from(this.predictions.values()).filter(
      (prediction) => prediction.userId === userId,
    );
  }

  async createPrediction(insertPrediction: InsertPrediction): Promise<Prediction> {
    const id = randomUUID();
    const prediction: Prediction = { 
      id,
      userId: insertPrediction.userId,
      species: insertPrediction.species,
      region: insertPrediction.region,
      timeframe: insertPrediction.timeframe,
      scenario: insertPrediction.scenario,
      fishPopulation: insertPrediction.fishPopulation || null,
      climateChange: insertPrediction.climateChange || null,
      geneticDiversity: insertPrediction.geneticDiversity || null,
      createdAt: new Date()
    };
    this.predictions.set(id, prediction);
    return prediction;
  }

  async searchPredictions(userId: string, query: string): Promise<Prediction[]> {
    const userPredictions = await this.getUserPredictions(userId);
    return userPredictions.filter(prediction => 
      prediction.species.toLowerCase().includes(query.toLowerCase()) ||
      prediction.region.toLowerCase().includes(query.toLowerCase())
    );
  }
}

export const storage = new MemStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// User profiles table for additional user data beyond Supabase auth
export const userProfiles = pgTable("user_profiles", {
  id: varchar("id").primaryKey(),
  email: text("email").notNull().unique(),
  fullName: text("full_name"),
  organization: text("organization"),
  role: text("role"),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

// AI Predictions table to store user predictions
export const predictions = pgTable("predictions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull(),
  species: text("species").notNull(),
  region: text("region").notNull(),
  timeframe: text("timeframe").notNull(),
  scenario: text("scenario").notNull(),
  fishPopulation: text("fish_population"),
  climateChange: text("climate_change"),
  geneticDiversity: text("genetic_diversity"),
  createdAt: timestamp("created_at").defaultNow(),
});

export const insertUserProfileSchema = createInsertSchema(userProfiles).pick({
  email: true,
  fullName: true,
  organization: true,
  role: true,
});

export const insertPredictionSchema = createInsertSchema(predictions).omit({
  id: true,
  createdAt: true,
});

export type InsertUserProfile = z.infer<typeof insertUserProfileSchema>;
export type UserProfile = typeof userProfiles.$inferSelect;
export type InsertPrediction = z.infer<typeof insertPredictionSchema>;
export type Prediction = typeof predictions.$inferSelect;

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { AuthProvider } from "@/contexts/AuthContext";
import Navigation from "@/components/navigation";
import Footer from "@/components/footer";
import Home from "@/pages/home";
import OceanData from "@/pages/ocean-data";
import FisheriesData from "@/pages/fisheries-data";
import MolecularData from "@/pages/molecular-data";
import AIPredictions from "@/pages/ai-predictions";
import About from "@/pages/about";
import Auth from "@/pages/auth";
import NotFound from "@/pages/not-found";

function Router() {
  return (
    <div className="min-h-screen flex flex-col">
      <Navigation />
      <main className="flex-1">
        <Switch>
          <Route path="/" component={Home} />
          <Route path="/ocean-data" component={OceanData} />
          <Route path="/fisheries-data" component={FisheriesData} />
          <Route path="/molecular-data" component={MolecularData} />
          <Route path="/ai-predictions" component={AIPredictions} />
          <Route path="/about" component={About} />
          <Route path="/auth" component={Auth} />
          <Route component={NotFound} />
        </Switch>
      </main>
      <Footer />
    </div>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <AuthProvider>
        <TooltipProvider>
          <Toaster />
          <Router />
        </TooltipProvider>
      </AuthProvider>
    </QueryClientProvider>
  );
}

export default App;

