      const response = await fetch("/api/ml-predict", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          query: searchQuery
        }),
      });

      const data = await response.json();

      // Check if it's a validation error (400 status)  
      if (!response.ok) {
        if (response.status === 400) {
          // Species validation error - show error message
          setRawModelOutput(null);
          setSearchResults([]);
          throw new Error(data.error || "Invalid species. Please search for marine species like tuna, salmon, cod, etc.");
